,        url: '/admin/administrators',        showRefresh: true,        showToggle: true,        showColumns: true,        method: 'get',        sortName:'login_name',        height: 600,        pageSize: 10,        pageNumber: 1,//开始的时候是第几页        pageList: [5, 10, 15, 25,'ALL'],        queryParamsType: '',        dataField:'data',//指定        sidePagination: "server",        toolbar: "#exampleToolbar",        iconSize: "outline",        sortOrder:"desc",        icons: {            refresh: "glyphicon-repeat",            toggle: "glyphicon-list-alt",            columns: "glyphicon-list",        }    });    function statusFormatter(value, row, index) {        if (value == 1) {            return '<a class="label label-primary defriend" href="#" data_id="'+row.uid+'">已激活</a>'        } else {            return '<a class="label label-danger defriend" href="#"  data_id="'+row.uid+'">已禁用</a>'        }    }    function showloginFormatter(value, row, index) {        return '<a class="" href="admin/loginlist/' + row.uid + '">查看</a>'    }    function groupFormatter(value, row, index) {        if(value.length){            var group = [];            for(var i=0; i<value.length;i++){                group.push(value[i]['title']);            }            return group.join('，');        }else{            return '无分组';        }    }    $(document).ready(function () {        // Tooltip        $('[data-toggle="tooltip"]').tooltip({            trigger: 'hover'        });       // user_operate($('#table'),'admin/add','admin/del','admin/edit/','admin/reset','admin/defriend');    });</script>@endsection