@extends('admin/layouts.base')@section('title', '主页')@section('body')    <body class="gray-bg">    <div class="wrapper wrapper-content animated fadeInRight">        <div class="ibox float-e-margins">            <div class="ibox-title">                <h5>选题开放时间                    <small>设置学生用户选题时间</small>                </h5>                <div class="ibox-tools">                </div>            </div>            <div class="ibox-content">                <form class="form-horizontal" id="setting">                    <div class="form-group">                        <label class="col-sm-2 control-label">开始时间</label>                        <div class="col-sm-10">                            <input type="text" class="form-control" name="begin_time" id="begin-time" required="required" value="{{$setting ? $setting->begin_time : ''}}">                        </div>                    </div>                    <div class="hr-line-dashed"></div>                    <div class="form-group">                        <label class="col-sm-2 control-label">结束时间</label>                        <div class="col-sm-10">                            <input type="text" class="form-control" name="end_time" id="end-time" required="required" value="{{$setting ? $setting->end_time : ''}}">                        </div>                    </div>                    <div class="hr-line-dashed"></div>                    <div class="form-group">                        <div class="col-sm-4 col-sm-offset-2">                            <button class="btn btn-primary" type="button" id="confirm">设置</button>                            <button class="btn btn-white" type="reset" id="reset">重置</button>                        </div>                    </div>                </form>            </div>        </div>    </div>    </body>@endsection@section('linkjs')    <script src="/js/plugins/laydate/laydate.js"></script>@endsection@section('js')    <script>        //日期时间选择器        laydate.render({            elem: '#begin-time',            type: 'datetime'        });        laydate.render({            elem: '#end-time',            type: 'datetime'        });        $("#confirm").click(function () {            if ($('#setting').validate().form()) {                //console.log($('#login').serialize());                $.ajax({                    url: '/admin/setting/time',                    type: 'POST',                    data:$('#setting').serialize(),                    success: function (result) {                        if (result.code) {                            //status为1成功                            layer.msg('' + result.msg, {icon: 1})                        } else {                            layer.msg('' + result.msg, {icon: 2});                        }                    }                })            }        });        $("#reset").click(function () {            $.ajax({                url: '/admin/setting/time',                type: 'POST',                data:{act:'reset'},                success: function (result) {                    if (result.code) {                        //status为1成功                        layer.msg('' + result.msg, {icon: 1})                    } else {                        layer.msg('' + result.msg, {icon: 2});                    }                }            })        });    </script>@endsection